import "./App.css";

const skills = {
  html: { color: "#FFEC5C", name: "HTML" },
  java: { color: "orangered", name: "Java" },
  cSharp: { color: "#9F55A7", name: "C#" },
  react: { color: "#006AB5", name: "React" },
};

const data = [
  {
    img: "https://t4.ftcdn.net/jpg/03/69/19/81/360_F_369198116_K0sFy2gRTo1lmIf5jVGeQmaIEibjC3NN.jpg",
    name: "Andrew Bear",
    profile:
      "Full-stack web developer and architect. Design is as Thanos is 'inevitable', the only way to go fast is to go well.",
    skills: [skills.html, skills.java, skills.cSharp, skills.react],
  },
];

function App() {
  return (
    <div>
      <ProfileCard value={data[0]} />
    </div>
  );
}

function ProfileCard(props) {
  return (
    <div className="profile-card">
      <img src={props.value.img} alt="programmer" />
      <div className="profile-card-data">
        <h2>{props.value.name}</h2>
        <p>{props.value.profile}</p>
        <div className="skills">
          {props.value.skills.map((skill) => {
            return (
              <span className="skill" style={{ backgroundColor: skill.color }}>
                {skill.name}
              </span>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default App;
