import "./index.css";
import React, { useState } from "react";

const messages = [
  "Learn React ‚öõÔ∏è",
  "Apply for jobs üíº",
  "Invest your new income ü§ë",
];

export default function App() {
  return (
    <>
      <DateCounter />
    </>
  );
}

function Steps() {
  const [step, setStep] = useState(1);

  const [isOpen, setIsOpen] = useState(true);

  function HandlePrevious() {
    setStep((s) => (s > 1 ? s - 1 : s));
  }

  function HandleNext() {
    setStep((s) => (s < 3 ? s + 1 : s));
  }

  return (
    <>
      <button className="close" onClick={() => setIsOpen((io) => !io)}>
        &times;
      </button>
      {isOpen && (
        <div className="steps">
          <div className="numbers">
            <div className={step >= 1 ? "active" : ""}>1</div>
            <div className={step >= 2 ? "active" : ""}>2</div>
            <div className={step >= 3 ? "active" : ""}>3</div>
          </div>
          <p className="message">
            Step {step}: {messages[step - 1]}
          </p>
          <div className="buttons">
            <button
              style={{ backgroundColor: "#7950F2", color: "#fff" }}
              onClick={HandlePrevious}
            >
              Previous
            </button>
            <button
              style={{ backgroundColor: "#7950F2", color: "#fff" }}
              onClick={HandleNext}
            >
              Next
            </button>
          </div>
        </div>
      )}
    </>
  );
}

function DateCounter() {
  const [step, setStep] = useState(1);
  const [count, setCount] = useState(0);

  const [date, setDate] = useState(new Date());

  const days = [
    "Sunday",
    "Monday",
    "Tuesday",
    "Wednesday",
    "Thursday",
    "Friday",
    "Saturday",
  ];

  const months = [
    "Jan",
    "Feb",
    "March",
    "Apr",
    "May",
    "Jun",
    "Jul",
    "Aug",
    "Sep",
    "Oct",
    "Nov",
    "Dec",
  ];

  function setDateByCount(count) {
    setCount(() => count);
    const currentDate = new Date();
    currentDate.setDate(currentDate.getDate() + count * step);
    setDate(() => currentDate);
  }

  return (
    <div style={{ textAlign: "center" }}>
      <Counter value={step} setValue={setStep} label={"Step"} />
      <Counter value={count} setValue={setDateByCount} label={"Count"} />
      <h2>
        Today is {days[date.getDay()]} {months[date.getMonth()]}{" "}
        {date.getDate()} {date.getFullYear()}
      </h2>
    </div>
  );
}

function Counter({ value, setValue, label }) {
  function handleIncreaseValue() {
    setValue(value + 1);
  }

  function handleReduceValue() {
    setValue(value - 1);
  }

  return (
    <div style={{ padding: "1%" }}>
      <button onClick={handleReduceValue}>-</button>
      <span>{label + ": " + value}</span>
      <button onClick={handleIncreaseValue}>+</button>
    </div>
  );
}
