import { useState } from "react";

function App() {
  const [items, setItems] = useState([]);

  function toggleItemPacked(id) {
    setItems((items) =>
      items.map((item) =>
        item.id === id ? { ...item, packed: !item.packed } : item
      )
    );
  }

  function removeItemWithId(id) {
    setItems((items) => items.filter((i) => i.id !== id));
  }

  function addItem(item) {
    setItems((items) => [...items, item]);
  }

  function getItemsCount() {
    return items.length;
  }

  function clearList() {
    const confirmed = window.confirm(
      "Are you sure you want to clear your list?"
    );

    setItems(() => []);
  }

  function getPackedCount() {
    return items.filter((item) => item.packed).length;
  }

  return (
    <div className="app">
      <Header />
      <Form onAddItem={addItem} />
      <PackingList
        initialItems={items}
        removeItemWithId={removeItemWithId}
        toggleItemPacked={toggleItemPacked}
        clearList={clearList}
      />
      <Stats getItemsCount={getItemsCount} getPackedCount={getPackedCount} />
    </div>
  );
}

function Header() {
  return <h1>üå¥ Far Away üíº</h1>;
}

function Form({ onAddItem }) {
  const [description, setDescription] = useState("");
  const [quantity, setQuantity] = useState(1);

  function handleSubmit(event) {
    event.preventDefault();

    if (!description) return;

    const newItem = { description, quantity, packed: false, id: Date.now() };
    console.log(newItem);

    onAddItem(newItem);

    setDescription("");
    setQuantity(1);
  }

  return (
    <form className="add-form" onSubmit={handleSubmit}>
      <h3>What do you need for your trip? üòé</h3>
      <select
        value={quantity}
        onChange={(e) => setQuantity(Number(e.target.value))}
      >
        {Array.from({ length: 20 }, (_, i) => i + 1).map((num) => (
          <option value={num} key={num}>
            {num}
          </option>
        ))}
      </select>
      <input
        type="text"
        placeholder="Item..."
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      />
      <button type="submit">Add</button>
    </form>
  );
}

function PackingList({
  initialItems,
  removeItemWithId,
  toggleItemPacked,
  clearList,
}) {
  const [sortCriteria, setSortCriteria] = useState("input");

  const sortByDescription = () => {
    return initialItems
      .slice()
      .sort((a, b) => a.description.localeCompare(b.description));
  };

  const sortByPackStatus = () => {
    return initialItems
      .slice()
      .sort((a, b) => Number(a.packed) - Number(b.packed));
  };

  const sortItems = () => {
    if (sortCriteria === "description") {
      return sortByDescription();
    } else if (sortCriteria === "packed") {
      return sortByPackStatus();
    } else return initialItems;
  };

  return (
    <div className="list">
      <ul style={{ overflow: "auto", height: "100%" }}>
        {sortItems().map((item) => (
          <Item
            item={item}
            key={item.id}
            removeItemWithId={removeItemWithId}
            toggleItemPacked={toggleItemPacked}
          />
        ))}
      </ul>

      <div className="actions">
        <select
          value={sortCriteria}
          onChange={(e) => setSortCriteria(e.target.value)}
        >
          <option value={"input"}>Sort by input order</option>
          <option value={"description"}>Sort by description</option>
          <option value={"packed"}>Sort by packed status</option>
        </select>

        <button onClick={() => clearList()}>Clear list</button>
      </div>
    </div>
  );
}

function Item({ item, removeItemWithId, toggleItemPacked }) {
  function handleRemoveItem() {
    removeItemWithId(item.id);
  }

  return (
    <li>
      <input
        type="checkbox"
        value={item.packed}
        onChange={() => toggleItemPacked(item.id)}
      />
      <span style={item.packed ? { textDecoration: "line-through" } : {}}>
        {item.quantity} {item.description}
      </span>
      <button onClick={handleRemoveItem}>‚ùå</button>
    </li>
  );
}

function Stats({ getItemsCount, getPackedCount }) {
  const percentage =
    getItemsCount() === 0
      ? 0
      : Math.round((getPackedCount() / getItemsCount()) * 100);
  const itemsString = getItemsCount().length !== 1 ? "items" : "item";

  return (
    <footer className="stats">
      <em>
        {percentage === 100
          ? "You got everything, have fun on your trip!"
          : "You have " +
            getItemsCount() +
            " " +
            itemsString +
            " on your list, and you have packed " +
            percentage +
            "%"}
      </em>
    </footer>
  );
}

export default App;
