import { useState } from "react";

function App() {
  const [items, setItems] = useState([]);

  function toggleItemPacked(id) {
    setItems((items) =>
      items.map((item) => {
        if (item.id === id) {
          item.packed = !item.packed;
        }
        return item;
      })
    );
  }

  function removeItemWithId(id) {
    setItems((items) => items.filter((i) => i.id !== id));
  }

  function addItem(item) {
    setItems((items) => [...items, item]);
  }

  function getItemsCount() {
    return items.length;
  }

  function getPackedCount() {
    return items.filter((item) => item.packed).length;
  }

  return (
    <div className="app">
      <Header />
      <Form onAddItem={addItem} />
      <PackingList initialItems={items} removeItemWithId={removeItemWithId} />
      <Stats getItemsCount={getItemsCount} getPackedCount={getPackedCount} />
    </div>
  );
}

function Header() {
  return <h1>🌴 Far Away 💼</h1>;
}

function Form({ onAddItem }) {
  const [description, setDescription] = useState("");
  const [quantity, setQuantity] = useState(1);

  function handleSubmit(event) {
    event.preventDefault();

    if (!description) return;

    const newItem = { description, quantity, packed: false, id: Date.now() };
    console.log(newItem);

    onAddItem(newItem);

    setDescription("");
    setQuantity(1);
  }

  return (
    <form className="add-form" onSubmit={handleSubmit}>
      <h3>What do you need for your trip? 😎</h3>
      <select
        value={quantity}
        onChange={(e) => setQuantity(Number(e.target.value))}
      >
        {Array.from({ length: 20 }, (_, i) => i + 1).map((num) => (
          <option value={num} key={num}>
            {num}
          </option>
        ))}
      </select>
      <input
        type="text"
        placeholder="Item..."
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      />
      <button type="submit">Add</button>
    </form>
  );
}

function PackingList({ initialItems, removeItemWithId }) {
  return (
    <div className="list">
      <ul style={{ overflow: "auto", height: "100%" }}>
        {initialItems.map((item) => (
          <Item item={item} key={item.id} removeItemWithId={removeItemWithId} />
        ))}
      </ul>
    </div>
  );
}

function Item({ item, removeItemWithId }) {
  function handleRemoveItem() {
    removeItemWithId(item.id);
  }

  return (
    <li>
      <input type="checkbox" value={item.packed} onChange={() => {}} />
      <span style={item.packed ? { textDecoration: "line-through" } : {}}>
        {item.quantity} {item.description}
      </span>
      <button onClick={handleRemoveItem}>❌</button>
    </li>
  );
}

function Stats({ getItemsCount, getPackedCount }) {
  return (
    <footer className="stats">
      You have {getItemsCount()} {getItemsCount() > 1 ? "items" : "item"} on
      your list, and you have packed {getPackedCount()}
    </footer>
  );
}

export default App;
